    private void btnExecutarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExecutarActionPerformed
        // TODO add your handling code here:
        try {
            Calculadora n = new Calculadora();

            int Ip1 = Integer.parseInt(jTIP1.getText());
            n.setIp1(Ip1);
            int Ip2 = Integer.parseInt(jTIP2.getText());
            n.setIp2(Ip2);
            int Ip3 = Integer.parseInt(jTIP3.getText());
            n.setIp3(Ip3);
            int Ip4 = Integer.parseInt(jTIP4.getText());
            n.setIp4(Ip4);
            int bitys = Integer.parseInt(jTBitys.getText());
            n.setBitys(bitys);

            String saida = "- IP DE REDE : " + n.getIp1() + "." + n.getIp2() + "." + n.getIp3() + "." + "0" +"/"+ n.getBitys()+"\n";
           // saida += "" + "\n";
            saida += "- MASCARA DE SUB-REDE: " + n.MascaraDeRede() + "\n";
           // saida += "" + "\n";
            saida += "- TOTAL DE IP: " + n.CalcularNumeroDeIp() + "\n";
            //saida += "" + "\n";
            saida += "- TOTAL DE IP PARA USO: " + n.CalculTotalIPsparaUso() + "\n";
            //saida += "" + "\n";
            saida += "- PRIMEIRO HOST: " + n.CalcularHostMinimo() + "\n";
            //saida += "" + "\n";
            saida += "- ÃšLTIMO HOST: " + n.CalcularUltimoHost() + "\n";
           // saida += "" + "\n";
            saida += "- BROADCAST: " + n.CalcularBroadcast() + "\n";
           // saida += "" + "\n";
            saida += "- RANGE IPS: " + n.getIp1() +"."+ n.getIp2()+"." + n.getIp3() +"."+ "0 -  " + n.CalcularRanges();

            jTSaida.setText(saida);

        } catch (Exception erro) {
            JOptionPane.showMessageDialog(rootPane, erro.getMessage());
        }
    }//GEN-LAST:event_btnExecutarActionPerformed

